namespace campanhabrinquedo.domain.Validacoes
{
    using System;
    using FluentValidation;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            

    public abstract class ValidacaoBase<T> : AbstractValidator<T> where T : class
    {
        protected void ValidaNome()
        {
            RuleFor(c => c.Nome)
            .NotEmpty().WithMessage("Nome n√£o pode ser em branco!")
            .Length(3, 150).WithMessage("Nome deve ter ao menos 3 caracteres!");
        }
    }
}